import React from 'react'

import { useEffect, useState } from 'react';


const Userpage = () => {
  const [user, setUser] = useState(null);
  const userId = sessionStorage.getItem('userId');


  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/Login-user/${userId}`);
        const data = await response.json();
        setUser(data);
      } catch (error) {
        console.log(error);
        // handle error and redirect to login page
        alert('An error occurred. Please login again.');
        // history.push('/login');
      }
    };

    fetchData();
  }, [userId]);

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <div className='row col-12'>
        <div className='col-3 bg-secondary'>
     
        </div>
        <div className='col-9' id='Dashboard'>

          <div className='bg-info mt-2'><h1>Welcome {user.name}!</h1></div>
          <div className='container'>
            <div className='row' >
              <div className='table-responsive'>

                <table className='table table-success'>

                  <thead>
                    <tr>
                      <th colSpan={2}>Your Profile</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <tr>
                        <th>Name</th>
                        <td>{user.name}</td>
                      </tr>
                      <tr>
                        <th>Email</th>
                        <td>{user.email}</td>
                      </tr>
                      <tr>
                        <th>Phone</th>
                        <td>{user.phone}</td>
                      </tr>
                      <tr>
                        <th>Address</th>
                        <td>{user.address}</td>
                      </tr>
                    </tr>
                  </tbody>

                </table>

              </div>
            </div>

          </div>

        </div>
      </div>

    </div>
  )
}

export default Userpage
