import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'

const AdminLogin = () => {

    const navigate = useNavigate();

    const empty = {
        name: "",
        password: ""
    }

    // state for handle the login inputs

    const [handle, setHandle] = useState(empty)

    const loginHandler = (e) => {
        const { name, value } = e.target;
        setHandle({ ...handle, [name]: value })
        console.log(handle)
    }


    //function for login
    const loginApi = async () => {
        if (handle.name === "" || handle.password === "") {
            alert("Please Enter the Fields First")
        } else {
            let record = await fetch("http://localhost:5000/Admin_Login", {
                method: "POST",
                headers: {
                    "content-Type": "application/json",
                },
                body: JSON.stringify(handle)
            })
            record = await record.json()
            if (record.message === "Login Successful") {

                // store id in SessionStorage
                sessionStorage.setItem("adminId", record.loginadmin._id)
                sessionStorage.setItem("adminEmail", record.loginadmin.name)
                alert("Login Successfully")
                navigate("/Admin")

            } else {
                alert("Username and Passwor is invalid")
            }
        }
    }


    return (
        <>
            <div style={{
                backgroundImage: 'url("./Image/quranadmin.jpg")',
                backgroundSize: 'cover',
            }}>
                <section className="vh-100 gradient-custom">
                    <div className="container py-5 h-100">
                        <div className="row justify-content-center align-items-center h-100">
                            <div className="col-12 col-lg-9 col-xl-7 ">
                                <div className="card shadow-2-strong card-registration" style={{ borderradius: '35px', backgroundColor: '#c1e5fd' }}>
                                    <div className="card-body p-4 p-md-5">
                                        <h3 className="mb-4 pb-2 pb-md-0 mb-md-5">Login as Patient</h3>
                                        <form
                                            onSubmit={(e) => {
                                                e.preventDefault()
                                            }}>
                                            <div className="row">
                                                <div className="col-12">

                                                    <div className="form-outline">
                                                        <input type="name" name='name' class="form-control form-control-lg" onChange={loginHandler} />
                                                        <label class="form-label" for="name">Name</label>
                                                    </div>
                                                </div>
                                            </div>

                                            <div className="row">
                                                <div className="col-12">
                                                    <div className="form-otline">
                                                        <input type="password" name='password' id="password" class="form-control form-control-lg" onChange={loginHandler} />
                                                        <label className="form-label" for="password">Password</label>
                                                    </div>
                                                </div>
                                            </div>

                                            <div className="mt-4 pt-2">
                                                <input onClick={loginApi} className="btn btn-primary btn-lg" type="submit" value="Submit" />
                                            </div>

                                        </form>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </div>
        </>
    )
}

export default AdminLogin
