import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import AdminNav from './AdminNav';

const Allstudnets = (props) => {

    const { editApi } = props;
    const [data, setData] = useState([]);

    const fetchApi = async () => {
        var value = await fetch("http://localhost:5000/doctors");
        value = await value.json();
        setData(value);
    };

    //function for deleting the data
    const delApi = async (id) => {
        // var data =
        await fetch(`http://localhost:5000/doctors/${id}`, {
            method: "DELETE",
        });
        // data = await data.json();
        fetchApi();
    };

    useEffect(() => {
        fetchApi();
    }, []);

    return (
        <div>
            <div className='row col-12'>
                <div className='col-3 bg-secondary'>
                    <AdminNav />
                </div>
                <div className='col-9' id='Dashboard'>
                    <div className='bg-info mt-2'><h1>All Teachers</h1></div>

                    <div className="container">
                        <div className="row bg-dark pt-2 pb-2">
                            {data.map((item) => (
                                <div className="col-md-3 mt-4 row">
                                    <div className="card " style={{ width: "16rem" }}>
                                        {/* <img className="card-img-top" src="..." alt="Card image cap" /> */}
                                        <div className="card-body">
                                            <h5 className="card-title">{item.name}</h5>
                                            <p className="card-text">{item.email}</p>
                                            <h2 className="text-success">{item.gender}</h2>
                                            <h2 className="text-success">{item.phone}</h2>
                                            <h2 className="text-success">{item.city}</h2>
                                            <h2 className="text-success">{item.time}</h2>
                                            <div className="d-flex justify-content-around">
                                                <Link to="/Updatedoctor">
                                                    <button
                                                        onClick={() => editApi(item._id)}
                                                        className="btn btn-primary"
                                                    >
                                                        Edit
                                                    </button>
                                                </Link>
                                                <button
                                                    onClick={() => delApi(item._id)}
                                                    className="btn btn-danger"
                                                >
                                                    delete
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
            {/* <h2 className="text-center display-3">Show All items</h2> */}


            {/* <table className="table table-bordered mt-5">
        <tr>
          <th>ID</th>
          <th>Title</th>
          <th>Description</th>
          <th>Price</th>
          <th>Action</th>
        </tr>
        {data.map((item) => (
          <tr>
            <td>{item._id}</td>
            <td>{item.title}</td>
            <td>{item.desc}</td>
            <td>{item.price}</td>
            <td>
              <Link to="/update">
                <button
                  onClick={() => editApi(item._id)}
                  className="btn btn-primary"
                >
                  Edit
                </button>
              </Link>
            </td>
            <td>
              <button
                onClick={() => delApi(item._id)}
                className="btn btn-danger"
              >
                delete
              </button>
            </td>
          </tr>
        ))}
      </table> */}
        </div>


    )
}

export default Allstudents
